const BASKET_OPEN=document.querySelector(".item__button--buy"),BASKET_POPAP=document.querySelector(".modal__basket"),BASKET_CLOSE=document.querySelector(".basket__close");BASKET_OPEN.addEventListener("click",e=>{e.preventDefault(),BASKET_POPAP.classList.remove("visually-hidden")}),BASKET_CLOSE.addEventListener("click",e=>{e.preventDefault(),BASKET_POPAP.classList.add("visually-hidden")});